import {
  copycat,
  // faker
} from '@snaplet/copycat'
import { defineConfig } from 'snaplet'

// import { tagName } from '@my/snaplet'

if (!process.env.SNAPLET_HASH_KEY) {
  throw new Error('SNAPLET_HASH_KEY is required')
}

copycat.setHashKey(process.env.SNAPLET_HASH_KEY)

export default defineConfig({
  select: {
    auth: {
      audit_log_entries: false,
      flow_state: false,
      identities: false,
      instances: false,
      mfa_amr_claims: false,
      mfa_challenges: false,
      mfa_factors: false,
      refresh_tokens: false,
      saml_providers: false,
      saml_relay_states: false,
      sessions: false,
      sso_domains: false,
      sso_providers: false,
      one_time_tokens: false,
    },
    storage: {
      buckets: true,
      objects: true,
      s3_multipart_uploads: false,
      s3_multipart_uploads_parts: false,
    },
    dbdev: false,
    extensions: false,
    graphql: false,
    graphql_public: false,
    pgsodium: false,
    pgsodium_masks: false,
    pgtle: false,
    realtime: false,
    vault: false,
    net: false,
    shovel: false,
    // @ts-ignore
    pgtap: false,
    public: {
      challenges: false,
      activity: false,
    },
    temporal: false,
  },
  subset: {
    keepDisconnectedTables: true,
    targets: [
      {
        table: 'auth.users',
        percent: 100,
      },
      {
        table: 'public.send_accounts',
        percent: 100,
      },
      // {
      //   table: 'public.activity',
      //   orderBy: 'activity.created_at desc',
      //   rowLimit: 100_000,
      // },
    ],
  },
  transform: {
    $mode: 'auto',
    auth: {
      users: ({ row }) => {
        let phone: string
        if (row.phone !== null) {
          phone = `1${copycat.phoneNumber(row.phone.slice(1), {
            length: {
              min: 11,
              max: 14, // max 15 including prefix
            },
          })}`
          // supabase does not store the + in the phone number
          phone = phone.replace('+', '')
        } else {
          phone = copycat.phoneNumber(row.phone)
        }

        return {
          ...row,
          email: copycat.email(row.email, {
            limit: 255,
          }),
          phone,
          password: false,
        }
      },
    },
    storage: {
      buckets: ({ row }) => {
        return row
      },
      objects: ({ row }) => {
        return row
      },
    },

    public: {
      // activity: ({ row }) => {
      //   return row
      // },
      profiles: ({ row }) => {
        return {
          ...row,
          name: copycat.fullName(row.name),
          about: copycat.sentence(row.about),
          referral_code: copycat.scramble(row.referral_code),
        }
      },
      send_accounts: ({ row }) => {
        return row
      },
      send_account_tags: ({ row }) => {
        return row
      },
      send_account_transfers: ({ row }) => {
        return row
      },
      send_account_created: ({ row }) => {
        return row
      },
      send_token_transfers: ({ row }) => {
        return row
      },
      send_token_v0_transfers: ({ row }) => {
        return row
      },
      tags: ({ row }) => {
        return row
      },
      distributions: ({ row }) => {
        return row
      },
      distribution_shares: ({ row }) => {
        return row
      },
      distribution_verification_values: ({ row }) => {
        return row
      },
      distribution_verifications: ({ row }) => {
        return row
      },
      receipts: ({ row }) => {
        return row
      },
      referrals: ({ row }) => {
        return row
      },
      tag_receipts: ({ row }) => {
        return row
      },
      send_slash: ({ row }) => {
        return row
      },
    },
  },
})
